-- Get base stats for a species
function pokemon_base_stats.getBaseStats(species_id)
    return species_data[species_id]
end

-- Get species name
function pokemon_base_stats.getSpeciesName(species_id)
    local data = species_data[species_id]
    return data and data.name or "Unknown"
end

-- Check if stats match expected base stats (for randomizer detection)
function pokemon_base_stats.checkStatsMatch(species_id, actual_stats)
    local base = species_data[species_id]
    if not base then
        return false
    end
    
    -- Allow for some variance due to DVs and level
    local tolerance = 0.3  -- 30% tolerance
    
    local differences = {
        math.abs(actual_stats.max_hp - base.hp) / base.hp,
        math.abs(actual_stats.attack - base.attack) / base.attack,
        math.abs(actual_stats.defense - base.defense) / base.defense,
        math.abs(actual_stats.speed - base.speed) / base.speed,
        math.abs(actual_stats.sp_attack - base.sp_attack) / base.sp_attack,
        math.abs(actual_stats.sp_defense - base.sp_defense) / base.sp_defense
    }
    
    for _, diff in ipairs(differences) do
        if diff > tolerance then
            return false
        end
    end
    
    return true
end

return pokemon_base_stats